div.script:

           function doHeaderSearch()
           {
               var keyword = $("#searchBox")[0].value.trim();
               keyword = keyword.encodeURI().encodeURI();
               keyword = keyword.replace(/\*/g, "%252a").replace(/\./g, "%252e");
               if (keyword.length > 0)
               {
                   window.location.href = "http://social.zune.net/search/" + keyword;
               }
           }

